mapper.select:
SELECT 
FROM SYSTEM_ACTIVITY
WHERE belongManager = #{belongManager}

    AND id = #{id}


    AND activityName LIKE '%${activityName}%'


    AND status = #{status}

SELECT 
FROM SYSTEM_ACTIVITY
WHERE 1 = 1

    AND id = #{id}


    AND activityName LIKE '%${activityName}%'


    AND status = #{status}


    AND belongManager = #{belongManager}
